CREATE FUNCTION wkp_mno_change_geom_type() RETURNS text
    LANGUAGE plpgsql
    AS $$
DECLARE
  def_vw_meetpunten_g varchar;
  def__vw_meetnet_meetpunt_g varchar;
  def_vw_meetnet_meetpunt_g varchar;
  def_vw_tijdwaarden_bestrijdingsmiddelen_csv varchar;
