 SELECT opt.opt_id,
    opt.observed_property_type_class,
    opt.result_type,
    opt.hoedanigheid_id,
    hdh.code AS "hoedanigheid.code",
    opt.object_group_id,
    ojg.name AS "object.code",
    opt.quantity_id,
    grh.code AS "grootheid.code",
    opt.taxa_group_id,
