     JOIN hydro_object hdo ON ((hdo.hdo_id = mrz.mrz_id)))
     JOIN vw_waterlichamen wln ON ((wln.db_id = hdo.hdo_id)))
     JOIN namespace_type ns ON ((ns.ns_id = mno.inspire_id_namespace_id)))
