CREATE VIEW vw_gwchemicalexemptiontype AS
 WITH gwb_oordeel_ruw AS (
         SELECT DISTINCT ON (hdo_gwb.hdo_id, gw_chemical_status.opt_id) hdo_gwb.hdo_id AS gwb_id,
            gwb_yearly_status.year_of_status AS jaar,
